username = #{username}, age = #{age}, gender = #{gender}, birth_date = #{birthDate}, email = #{email}, phone = #{phone}, qq = #{qq}, province = #{province}, city = #{city}, introduction = #{introduction}, update_time = NOW(),
WHERE id = #{id}

